import React, { Component,useState,useEffect } from 'react';
import {  Form, Button, Col, Row } from 'react-bootstrap';
import { getData, postData, updateData,deleteData } from '../../store/Action/FormCrud';
import { connect } from 'react-redux';
import { useDispatch} from "react-redux";

const AddEditForm =(props)=>{	
    // const task= useSelector(state => state.FormCrudReducer.data);
	const dispatch = useDispatch();
const [description,setDescription]=useState(props.data.description)
const [id,setId]=useState(props.data.id);
const [date,setDate]=useState(props.data.date);
const[time,setTime]=useState(props.data.time);
const[assignedTo,setAssignedTo]=useState(props.data.assignedTo)

useEffect(() => {
    if(props.detectOps === "edit")
        {
                        console.log('working as expected')                                                                
						setDescription(props.data.task_msg);
						setId(props.data.id);
						setDate(new Date(props.data.task_date).toLocaleDateString('fr-CA'))
						setTime(props.data.task_time)
						setAssignedTo(props.data.assigned_user)						                                      
        }
})

const submit = (e) => {
			
    e.preventDefault();
    
    var hms = time;   // your input string
    var a = hms.split(':'); // split it at the colons				
    var seconds = (+a[0]) * 60 * 60 + (+a[1]) * 60 ;
    const params = {				
        task_msg:description,
        task_date:date,
        task_time: seconds,
        is_completed:1,		
        assigned_user:assignedTo,
        time_zone:-330
    }
    
    if (id === '') {		
            
    //props.postData(params);	
    dispatch(postData(params))
    props.close();	
    
    setTimeout(() => {
        props.updateCompo();    
    }, 2000);
    
    

    } else {
        dispatch(updateData(params, id))
            //this.props.updateData(params, id);
            props.close();	     
            setTimeout(() => {
                props.updateCompo();    
            }, 1000);
            
    

    }

};

const del =()=> {
    const deleteId=id 

    if (deleteId) {
        dispatch(deleteData(id))
        props.close();	
        setTimeout(() => {
            props.updateCompo();    
        }, 1000);
    }

}


return(
    <div>
          <Form  onSubmit={submit}>

<Form.Row>
    <Form.Group as={Col} xs={12} sm={12} md={12} lg={12} xl={12} >
        <Form.Label>Task Description</Form.Label>
        <Form.Control
            required
            type="text"
            placeholder="Description if task"            
            onChange={e => setDescription(e.target.value)}
            value={description}
            name="description"
        />
    </Form.Group>
</Form.Row>

<Form.Row className="acount-filled">
    <Form.Group as={Col} xs={6} sm={6} md={6} lg={6} xl={6} >
        <Form.Label>Date</Form.Label>
        <Form.Control
            required
            type="date"            
            onChange={e => setDate(e.target.value)}
            placeholder="date"
            value={date}
            name="date"

        />
    </Form.Group>
    <Form.Group as={Col} xs={6} sm={6} md={6} lg={6} xl={6} >
        <Form.Label>Time</Form.Label>
        <Form.Control

            type="time"
            
            onChange={e => setTime(e.target.value)}
            placeholder="time"
            value={time}
            name="time"


        />
    </Form.Group>
</Form.Row>
<Form.Row className="acount-filled">
    <Form.Group as={Col} xs={12} sm={12} md={12} lg={12} xl={12} >
        <Form.Label>Assign User</Form.Label>
        <Form.Control
            required
            type="text"            
            onChange={e => setAssignedTo(e.target.value)}
            placeholder="Assign User"
            value={assignedTo}
            name="assignedTo"

        />
    </Form.Group>
</Form.Row>
<Row>
    <Col md={1} sm={1} lg={1}>
    </Col>
    <Col md={4} sm={4}lg={4}>
        {id === '' ? 
        ""
        :<h4>
            
            <i class="fa fa-trash-o" aria-hidden="true" onClick={del }></i>
        </h4>
        }
        
    </Col>
    <Col md={1} sm={1} lg={1}>
    </Col>
    <Col md={6} sm={6}>
        <Button variant="outline-secondary"  type="button" onClick={props.close} className="formBtn" >
            Cancel
          </Button>
         &nbsp;
        <Button variant="success" type="submit" className="formBtn">
           Save
          </Button>
        
        
        
    </Col>
</Row>
</Form>
    </div>
)



}
export default AddEditForm

