import React, { Component,useEffect,useState,useRef } from 'react';
import axios from 'axios';
import { connect,useDispatch,useSelector } from 'react-redux';
import store from '../../store/index'
import { Container,  Card, ListGroup,Col, Row } from 'react-bootstrap';
import { getData, postData, updateData } from '../../store/Action/FormCrud';
import './Form.css';
import API from '../../API/Api';
import AddEditForm from './AddEditForm';
import Header from '../Header/Header';
import { faStethoscope } from '@fortawesome/free-solid-svg-icons';


const FormCrud=()=> {
	const task= useSelector(state => state.FormCrudReducer.data);
	const dispatch = useDispatch();
const [description,setDescription]=useState('')
const [id,setId]=useState('');
const [date,setDate]=useState(new Date().toLocaleDateString('fr-CA'));
const[time,setTime]=useState('00:00');
const[assignedTo,setAssignedTo]=useState('')
const[hide,setHide]=useState(true);
const [listTask,setListTask]=useState([]);
const[formOps,setformOps]=useState('');
const [result,setResult]=useState('');



useEffect(() => {
	const token = JSON.parse(localStorage.getItem('token'));		
		axios.interceptors.request.use(function (config) {
            config.headers.common['Authorization'] = 'Bearer ' +token;	
			config.headers.common['Accept'] = 'application/json';
			 config.headers.common['Content-Type'] = 'application/json';

			return config;
		})

		
		dispatch(getData())
		
		setTimeout(() => {
			console.log(store.getState())	
			 setListTask(store.getState().FormCrudReducer.data)
		}, 1000);
		
		// setListTask(task)
},[])

function getTaskData(){
	useEffect(() => {
		dispatch(getData())
		setTimeout(() => {
			console.log('hiting')
			console.log(store.getState())	
			 setListTask(store.getState().FormCrudReducer.data)
		}, 1000);
	},[])
	
}
const convertTime=(data)=> {
		let time = Number(data);	
		let hrs = Math.floor(time / 3600);
		let min = Math.floor(time % 3600 / 60);		
		return ( ('0' + hrs).slice(-2) + ":" +('0' + min).slice(-2)  )
	}
		const closeForm = () => {
			setHide(true)
			setId('')		
	};

	const displayForm = () => {
		setHide(false)
		setformOps('add')		
		
	};	
const edit = (i) => {
		
setId(i.id)
		
		if (i.id) {
			
			axios
				.get(`${API.baseUrl}/task/lead_58be137bfde045e7a0c8d107783c4598/${i.id}`)
				.then(response => response.data)
				.then(
					result => {												
						console.log(result) 
						
						   
						setResult(result);
						setDescription(result.results.task_msg);
						setId(result.results.id);
						setDate(new Date(result.results.task_date).toLocaleDateString('fr-CA'))
						setTime(convertTime(result.results.task_time))
						setAssignedTo(result.results.assigned_user)
						setHide(false)
						setformOps('edit');
																																								
					},
					err => {
						console.log(err);
					}
				);
		}

	};	

return(
	<div>
					<div>
				<Header />
				<Container className="formHead">
					
					<Row>
						<Col>
						</Col>
						
						<Col>
						<Card style={{ width: '28rem' }}>
							<Card.Header >TASKS {listTask.length?listTask.length:0} 
							<a  onClick={displayForm} href> 
							<h5 className="float-right hover ">
								<i class="fa fa-plus-circle" aria-hidden="true"></i>
							</h5>
							</a>
							</Card.Header >
							<ListGroup variant="flush">
								<ListGroup.Item hidden={hide}
								className="addUpdateForm"
								>
									<AddEditForm
									 close={closeForm}
									 updateCompo={getTaskData}
									 data={{
                                          description,date,time,assignedTo,id
									 }}
									 detectOps={formOps}									 									 									 
									 />

								
								</ListGroup.Item>
								{listTask.map(data => (
									<ListGroup.Item>
										<Row>
											<Col lg={1} md={1} sm={1}>
											<h3>
											<i class="fa fa-user-circle" aria-hidden="true"></i>
										    </h3>
											</Col>

											
											<Col lg={11} sm={11} md={11}>
											<h5>										
											{data.task_msg}</h5>										
											{new Date(data.task_date).toLocaleDateString('fr-CA')}
											<h5 
											 onClick={()=>{edit(data)}} 
											style={{ marginTop: '-25px' }} className=" float-right hideEdit hover">
											<i class="fa fa-pencil" aria-hidden="true"></i>
											</h5>
											</Col>
										
										
										</Row>
										
									</ListGroup.Item>
								))}
							</ListGroup>
						</Card>

						</Col>

						<Col>
						</Col>
					</Row>																
				</Container>
			</div>

	</div>
);
}
